insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'The Shawshank Redemption', 'Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.', 'R', 'DRAMA', to_date('10/14/1994','mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'The Godfather', 'The aging patriarch of an organized crime dynasty in postwar New York City transfers control of his clandestine empire to his reluctant youngest son.', 'R', 'DRAMA', to_date('03/24/1972', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'The Dark Knight', 'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham, Batman must accept one of the greatest psychological and physical tests of his ability to fight injustice.', 'PG13', 'ACTION', to_date('07/18/2008', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'The Godfather Part II', 'The early life and career of Vito Corleone in 1920s New York City is portrayed, while his son, Michael, expands and tightens his grip on the family crime syndicate.', 'R', 'DRAMA', to_date('12/18/1974', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, '12 Angry Men', 'The jury in a New York City murder trial is frustrated by a single member whose skeptical caution forces them to more carefully consider the evidence before jumping to a hasty verdict.', 'PG', 'DRAMA', to_date('04/10/1957', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'Schindlers List', 'In German-occupied Poland during World War II, industrialist Oskar Schindler gradually becomes concerned for his Jewish workforce after witnessing their persecution by the Nazis.', 'R', 'DRAMA', to_date('02/04/1993', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'The Lord of the Rings: The Return of the King', 'Gandalf and Aragorn lead the World of Men against Saurons army to draw his gaze from Frodo and Sam as they approach Mount Doom with the One Ring.', 'PG13', 'DRAMA', to_date('12/17/2003', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'Pulp Fiction', 'The lives of two mob hitmen, a boxer, a gangster and his wife, and a pair of diner bandits intertwine in four tales of violence and redemption.', 'R', 'DRAMA', to_date('10/14/1994', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'The Lord of the Rings: The Fellowship of the Ring', 'A meek Hobbit from the Shire and eight companions set out on a journey to destroy the powerful One Ring and save Middle-earth from the Dark Lord Sauron.', 'PG13', 'DRAMA', to_date('12/19/2001', 'mm-dd-yyyy'));
insert into MOVIES (title_id, title, description, rating, category, release_date) values (movie_id_seq.NEXTVAL, 'The Good, the Bad and the Ugly', 'A bounty hunting scam joins two men in an uneasy alliance against a third in a race to find a fortune in gold buried in a remote cemetery.', 'R', 'ACTION', to_date('12/29/1966', 'mm-dd-yyyy'));
